const purchaseButtons=document.querySelectorAll(".buy"),purchasePopup=document.querySelector(".modal-add"),purchaseClose=purchasePopup.querySelector(".modal-close");purchaseButtons.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),purchasePopup.classList.add("modal-show")})}),purchaseClose.addEventListener("click",function(e){e.preventDefault(),purchasePopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){"Esc"!==e.key&&"Escape"!==e.key||purchasePopup.classList.contains("modal-show")&&(e.preventDefault(),purchasePopup.classList.remove("modal-show"))});
