const link=document.querySelector(".connection-button"),popup=document.querySelector(".modal-connection"),close=popup.querySelector(".modal-close"),form=popup.querySelector(".connection"),login=popup.querySelector(".user-name"),email=popup.querySelector(".user-email");let isStorageSupport=!0,storage="";try{storage=localStorage.getItem("login")}catch(e){isStorageSupport=!1}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),storage?(login.value=storage,email.focus()):login.focus()}),form.addEventListener("submit",function(e){login.value&&email.value?isStorageSupport&&localStorage.setItem("login",login.value):e.preventDefault()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){"Esc"!==e.key&&"Escape"!==e.key||popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"))});
